import axios from "axios";
import {stringify} from "query-string";


function addCartItem(params){
  return axios.post(`/cart`, params);
};
function listCartItems(params){
  return axios.get(`/cart?${stringify(params)}`);
};
function removeCartItem(id){
  return axios.get(`/cart/${id}`);
};


export {
  addCartItem,
  listCartItems,
  removeCartItem
};
