import React, {useState, useEffect, Fragment} from 'react';
import CartItem from './CartItem';
import Modal from "../modal/";


function deserialize(str){
  return JSON.parse(str);
};
function serialize(obj){
  return JSON.stringify(obj);
};
function addItem (item){
  const cartStorage = deserialize(localStorage.cartStorage || "[]");
  cartStorage.push(item);
  localStorage.cartStorage = serialize(cartStorage);
};
function removeItem(item){
  const cartStorage = deserialize(localStorage.cartStorage || "[]");
  localStorage.cartStorage = cartStorage.filter((i) => i.id !== item.id);
};
function clearCart(){
  localStorage.clear();
};
function subscribeToCartChanges(callback){
  window.addEventListener("storage", (evt) => {
    console.log("subscribeToCartChanges: ", evt);
    callback && callback(evt);
  });
};

const cartItems = [{name: "adfaf"}, {name: "e9koajsd"}, {name: "Laptops 101"}];

const Cart = ({}) => {
  console.log("Cart: ");
  const[items, setItems] = useState([]);
  const[open, setOpen] = useState(false);
  const onRemove = (item) => {
