    };
  };

  return (
    <div className="cart">
      <button className="button is-light"
        onClick={()=>{
          setOpen(true);
        }}>
        Cart [56]
      </button>

      <Modal
        open={open}
        title="Cart"
        onClose={()=>{
          console.log("onClose");
          setOpen(false);
        }}
        footer={(
          <div className="cart-footer">
            <button className="button is-danger is-uppercase">
              Order
            </button>
          </div>
        )}>
        <div>
          {items && (
            <div className="list">
              {items.map((item, index) => (
                <div key={index} className="list-item">
                  <button className="button is-small is-pulled-right is-danger"
                    onClick={onRemove(item)}>
                    <i className="fas fa-trash-alt"></i>
                  </button>
                  <div className="title is-capitalized">{item.name}</div>
                  <div className="subtitle">{item.price}</div>
                  <div className="subtitle">{item.name}</div>
                    <div className="field has-addons is-grouped is-grouped-right">
                      <p className="control">
                        <button className="button is-light">
                          <span className="icon">
                            <i className="fas fa-angle-left"/>
                          </span>
                        </button>
                      </p>
                      <p className="control">
                        <input className="input" type="number" placeholder="Quantity"/>
                      </p>
                      <p className="control">
                        <button className="button is-light">
                          <span className="icon">
                            <i className="fas fa-angle-right"/>
                          </span>
                        </button>
                      </p>
                    </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </Modal>

    </div>
  );
};

export default Cart;
export {addItem};
