import React, {useState, useEffect, Fragment} from 'react';
import CartItem from './CartItem';
import Modal from "../modal/";
import {orderProducts} from "../../apis/product";

function deserialize(str){
  try{
    return JSON.parse(str);
  }catch(ex){
    return [];
  }
};
function serialize(obj){
  try{
    return JSON.stringify(obj);
  }catch(ex){
    return "[]";
  }
};
function addItem (item){
  item.quantity = 1;
  const cartStorage = deserialize(localStorage.cartStorage || "[]");

  cartStorage.push(item);
  localStorage.cartStorage = serialize(cartStorage);
  window.dispatchEvent(new Event("storage"));
};
function removeItem(item){
  const cartStorage = deserialize(localStorage.cartStorage || "[]");
  const items = cartStorage.filter((i) => i.id !== item.id);
  localStorage.cartStorage = items;
  return items;
};
function listItems(){
  return deserialize(localStorage.cartStorage || "[]");
};
function clearCart(){
  localStorage.clear();
};
function subscribeToCartChanges(callback){
  window.addEventListener("storage", (evt) => {
    callback && callback(evt);
  });
};

const Cart = ({}) => {
  console.log("Cart: ");
  const[items, setItems] = useState([]);
  const[open, setOpen] = useState(false);
  const[finalize, setFinalize] = useState(false);

  const onRemove = (item) => {
    return () => {
      setItems(removeItem(item));
    };
  };

  let totals = 0;
  items.forEach((item) => {
    totals += item.price * item.quantity;
  });

  useEffect(()=>{
    setItems(listItems());
  },[]);

  console.log("items# ", items);

  return (
    <div className="cart">
      <button className="button is-light"
        onClick={()=>{
          setOpen(true);
        }}>
        Cart &nbsp;
        <span className="tag is-warning">
          {items && items.length}
        </span>
      </button>

      <Modal
        open={open}
        title="Cart"
        subtitle={(
          <Fragment>
            Total: {totals}
          </Fragment>
        )}
        onClose={()=>{
          console.log("onClose");
          setOpen(false);
        }}
        footer={(
          <div className="cart-footer">
            {items && items.length > 0 && (
              <button className="button is-danger is-large is-uppercase"
                onClick={() => {
                  orderProducts({items})
                  .then((r) => {
                    clearCart();
                    setFinalize(true);
                    setOpen(false);
                  })
                  .catch((err) => {});
                }}>
                Order
              </button>
            )}
          </div>
        )}>
        <div>
          {items && (
            <div className="list-">
              {items.map((item, index) => (
                <div key={index} className="list-item">
                  <button className="button is-small is-pulled-right is-danger"
                    onClick={onRemove(item)}>
                    <i className="fas fa-trash-alt"></i>
                  </button>
                  <div className="title is-capitalized">{item.name}</div>
                  <div className="subtitle">{item.price}</div>
                  <div className="subtitle">{item.name}</div>
                    <div className="field has-addons is-grouped is-grouped-right">
                      <p className="control">
                        <button className="button is-light">
                          <span className="icon">
                            <i className="fas fa-angle-left"/>
                          </span>
                        </button>
                      </p>
                      <p className="control">
                        <input className="input" type="number" placeholder="Quantity"/>
                      </p>
                      <p className="control">
                        <button className="button is-light">
                          <span className="icon">
                            <i className="fas fa-angle-right"/>
                          </span>
                        </button>
                      </p>
                    </div>
                </div>
              ))}
            </div>
          )}
          {(!items || !items.length) && (
            <p className="subtitle">
              No items in cart yet, browse to add items.
            </p>
          )}
        </div>
      </Modal>

    </div>
  );
};

export default Cart;
export {addItem, subscribeToCartChanges};
