import React, {useState, useEffect, Fragment} from 'react';
import {BrowserRouter as Router, Route, Switch, Link} from 'react-router-dom';
import Products from "./pages/product/Products";
import ProductView from "./pages/product/ProductView";
import Navbar from "./components/nav";
import logo from './logo.svg';



function App() {
  const[cart, setCart] = useState([]);

  const onAddToCart = (product) => {
    cart.push(product);
    console.log("cart: ", product, cart);
    setCart(cart);
  };
  const onRemoveFromCart = (product) => {
    const products = cart.filter((p) => {
      return p.id !== product.id;
    });
    console.log("removeFromCart: ", product, products);
    setCart(products);
  };

  return (
    <section className="section">
      <div className="container">
        <Router>
          <Navbar cart={cart} onRemoveFromCart={onRemoveFromCart}/>
          <Switch>
            <Route exact={true} path="/"
              render={()=>(
                <div className="has-text-centered">
                  <Link to="/products" className="button is-danger is-large
                    is-uppercase is-family-monospace">
                    Enter
                  </Link>
                </div>
              )}/>
            <Route exact={true} path="/products"
              render={()=>(
                <Products onAddToCart={onAddToCart}/>
              )}
            />
            <Route exact={true} path="/product/:id"
              render={()=>(
                <ProductView onAddToCart={onAddToCart}/>
              )}
            />
          </Switch>
        </Router>
      </div>
    </section>
  );
}

export default App;
