import React from 'react';
import Card from "../card";

function Product({id, name, description, price, image, category, reviews, quantityInStock, onClick, extended}) {

  return (
    <div className="product">

      <div className="columns is-multiple is-variable">
        <div className="column">
          <Card
            title={name}
            image={image}
            onClick={onClick}>
            {extended && (
              <div className="has-text-grey">{description}</div>
            )}
            <h1 className="subtitle">$ {price}</h1>
            <span className="has-text-grey is-size-7 is-capitalized">
              reviews <span className="tag is-warning">{reviews}</span>
            </span>
            <span className="has-text-grey is-size-7 is-pulled-right is-capitalized">
              Instock <span className="tag">{quantityInStock}</span>
            </span>
            <hr/>
            <div className="">
              <button className="button is-large is-fullwidth">Large</button>
            </div>
          </Card>
        </div>
        {extended && (
          <div className="column is-7">
            <div>{description}</div>
          </div>
        )}
      </div>

    </div>
  );
}

export default Product;
