import axios from "axios";
import {stringify} from "query-string";


async function getProduct(id){
  return await axios.get(`/product/${id}`).data;
};
async function listProducts(params){
  return await axios.get(`/products?${stringify(params)}`).data;
};

async function orderProducts(params){
  return await axios.post(`/order`, params).data;
};


export {
  getProduct,
  listProducts,
  orderProducts
};
