import React, {useState, useEffect, Fragment} from 'react';
import Modal from "../modal/";
import {orderProducts} from "../../apis/product";
import {
  removeItem, updateItem, listItems, clearCart, subscribeToCart
} from "./CartStorage";

// function addItem (item){
//   const cartStorage = deserialize(localStorage.cartStorage || "[]");
//   const existing = cartStorage.find((el) => el.id === item.id);
//   if(existing){
//     existing.quantity++;
//   }else{
//     cartStorage.push(item);
//   };
//   localStorage.cartStorage = serialize(cartStorage);
//   window.dispatchEvent(new Event("storage"));
// };
// function removeItem(item){
//   const cartStorage = deserialize(localStorage.cartStorage || "[]");
//   const items = cartStorage.filter((i) => i.id !== item.id);
//   localStorage.cartStorage = items;
//   return items;
// };
// function listItems(){
//   return deserialize(localStorage.cartStorage || "[]");
// };
// function clearCart(){
//   localStorage.clear();
// };
// function subscribeToCartChanges(callback){
//   window.addEventListener("storage", (evt) => {
//     callback && callback(evt);
//   });
// };





const Cart = ({}) => {
  console.log("Cart: ");
  const[items, setItems] = useState([]);
  const[open, setOpen] = useState(false);
  const[finalize, setFinalize] = useState(false);

  const onRemove = (item) => {
    return () => {
      removeItem(item);
    };
  };

  const updateQuantity = (item, qty) => {
    return (evt) => {
      console.log("updateQuantity#item ", item, qty);
      updateItem(item, qty);
    };
  };

  const computeTotal = (itms) => {
    let total = 0;
    itms.forEach((itm) => {
      total += parseInt(itm.price, 10) * itm.quantity;
    });
    return total;
  };

  useEffect(()=>{
    setItems(listItems());
    subscribeToCart((evt) => {
      setItems(listItems());
      console.log("Cart has changed!", evt, listItems());
    });
  },[]);


  return (
    <div className="cart">
      <button className="button is-light"
        onClick={()=>{
          setOpen(true);
        }}>
        Cart &nbsp;
        <span className="tag is-warning">
          {items && items.length}
        </span>
      </button>

      <Modal
        open={open}
        title="Cart"
        subtitle={(
          <Fragment>
            {computeTotal(items)}
          </Fragment>
        )}
        onClose={()=>{
          console.log("onClose");
          setOpen(false);
        }}
        footer={(
          <div className="cart-footer">
            {items && items.length > 0 && (
              <button className="button is-danger is-large is-uppercase"
                onClick={() => {
                  orderProducts({items})
                  .then((r) => {
                    clearCart();
                    setFinalize(true);
                    setOpen(false);
                  })
                  .catch((err) => {});
                }}>
                Order
              </button>
            )}
          </div>
        )}>
        <div>
          {items && (
            <div className="list-">
              {items.map((item, index) => (
                <div key={index} className="list-item">
                  <button className="button is-small is-pulled-right is-danger"
                    onClick={onRemove(item)}>
                    <i className="fas fa-trash-alt"/>
                  </button>
                  <div className="title is-capitalized">{item.name}</div>
                  <div className="subtitle">{item.price}</div>
                  <div className="subtitle">{item.name}</div>
                    <div className="field has-addons is-grouped-right">
                      <p className="control">
                        <button className="button is-light"
                          onClick={updateQuantity(item, -1)}>
                          <span className="icon">
                            <i className="fas fa-angle-left"/>
                          </span>
                        </button>
                      </p>
                      <p className="control">
                        <a className="button">
                          {item.quantity}
                        </a>
                      </p>
                      <p className="control">
                        <button className="button is-light"
                          onClick={updateQuantity(item, 1)}>
                          <span className="icon">
                            <i className="fas fa-angle-right"/>
                          </span>
                        </button>
                      </p>
                    </div>
                </div>
              ))}
            </div>
          )}
          {(!items || !items.length) && (
            <p className="subtitle">
              No items in cart yet, browse to add items.
            </p>
          )}
        </div>
      </Modal>

    </div>
  );
};

export default Cart;
