function addItem(item){
  const cart = new Map(deserialize(localStorage.cart || []));
  if(cart.has(item.id)){
    cart.get(item.id).quantity++;
  }else{
    item.quantity = 1;
    cart.set(item.id, item);
  };
  localStorage.cart = serialize(cart.entries());
  window.dispatchEvent(new Event("storage"));
};
function removeItem(item){
  const cart = new Map(deserialize(localStorage.cart || []));
  cart.delete(item.id);
  localStorage.cart = serialize(cart.entries());
  window.dispatchEvent(new Event("storage"));
};
function clearCart(){
  const cart = new Map(deserialize(localStorage.cart || []));
  cart.clear();
  localStorage.cart = serialize(cart.entries());
  window.dispatchEvent(new Event("storage"));
};
function listItems(){
  const cart = new Map(deserialize(localStorage.cart || []));
  return cart.entries();
};
function subscribeToCartChanges(callback){
  window.addEventListener("storage", (evt) => {
    callback && callback(evt);
  });
};



const Cart = ({}) => {
  console.log("Cart: ");
  const[items, setItems] = useState([]);
  const[open, setOpen] = useState(false);
  const[finalize, setFinalize] = useState(false);

  const onRemove = (item) => {
    return () => {
      setItems(removeItem(item));
    };
  };

  const updateQuantity = (item, dir) => {
    return (evt) => {
      console.log("updateQuantity#item ", item, dir);
    };
  };

  useEffect(()=>{
    setItems(listItems());
  },[]);

  console.log("items# ", items);

  return (
    <div className="cart">
      <button className="button is-light"
        onClick={()=>{
          setOpen(true);
        }}>
        Cart &nbsp;
        <span className="tag is-warning">
          {items && items.length}
        </span>
      </button>

      <Modal
        open={open}
        title="Cart"
        subtitle={(
          <Fragment>

          </Fragment>
        )}
        onClose={()=>{
          console.log("onClose");
          setOpen(false);
        }}
        footer={(
          <div className="cart-footer">
            {items && items.length > 0 && (
              <button className="button is-danger is-large is-uppercase"
                onClick={() => {
                  orderProducts({items})
                  .then((r) => {
                    clearCart();
                    setFinalize(true);
                    setOpen(false);
                  })
                  .catch((err) => {});
                }}>
                Order
              </button>
            )}
          </div>
        )}>
        <div>
          {items && (
            <div className="list-">
              {items.map((item, index) => (
                <div key={index} className="list-item">
                  <button className="button is-small is-pulled-right is-danger"
                    onClick={onRemove(item)}>
                    <i className="fas fa-trash-alt"></i>
                  </button>
                  <div className="title is-capitalized">{item.name}</div>
                  <div className="subtitle">{item.price}</div>
                  <div className="subtitle">{item.name}</div>
                    <div className="field has-addons is-grouped is-grouped-right">
                      <p className="control">
                        <button className="button is-light"
                          onClick={updateQuantity(item, "left")}>
                          <span className="icon">
                            <i className="fas fa-angle-left"/>
                          </span>
                        </button>
                      </p>
                      <p className="control">
                        <label className="label">
                          {item.quantity}
                        </label>
                      </p>
                      <p className="control">
                        <button className="button is-light"
                          onClick={updateQuantity(item, "right")}>
                          <span className="icon">
                            <i className="fas fa-angle-right"/>
                          </span>
                        </button>
                      </p>
                    </div>
                </div>
              ))}
            </div>
          )}
          {(!items || !items.length) && (
            <p className="subtitle">
              No items in cart yet, browse to add items.
            </p>
          )}
        </div>
      </Modal>

    </div>
  );
};

export default Cart;
export {addItem, subscribeToCartChanges};
